#!/usr/bin/env python3\n\"\"\"Работа с двоичными данными и потоками ввода-вывода.\"\"\"\n\nimport struct\nimport os\n\n\nclass BinaryIOExample:\n    \"\"\"Примеры работы с двоичным вводом-выводом.\"\"\"\n\n    @staticmethod\n    def write_binary_data(filename, data):\n        \"\"\"Запись двоичных данных в файл.\n        \n        Args:\n            filename: Имя файла\n            data: Данные для записи (список байтов или bytes)\n        \"\"\"\n        try:\n            with open(filename, 'wb') as f:\n                if isinstance(data, list):\n                    f.write(bytes(data))\n                else:\n                    f.write(data)\n            print(f\"✓ Данные успешно записаны в {filename}\")\n        except IOError as e:\n            print(f\"✗ Ошибка при записи файла: {e}\")\n\n    @staticmethod\n    def read_binary_data(filename):\n        \"\"\"Чтение двоичных данных из файла.\n        \n        Args:\n            filename: Имя файла\n            \n        Returns:\n            Прочитанные двоичные данные\n        \"\"\"\n        try:\n            with open(filename, 'rb') as f:\n                data = f.read()\n            print(f\"✓ Данные успешно прочитаны из {filename}\")\n            print(f\"  Размер: {len(data)} байт\")\n            return data\n        except FileNotFoundError:\n            print(f\"✗ Файл {filename} не найден\")\n            return None\n        except IOError as e:\n            print(f\"✗ Ошибка при чтении файла: {e}\")\n            return None\n\n\nif __name__ == \"__main__\":\n    os.makedirs('data', exist_ok=True)\n    BinaryIOExample.write_binary_data('data/test.bin', [72, 101, 108, 108, 111])\n    BinaryIOExample.read_binary_data('data/test.bin')\n", "sha": "", "_tool_input_summary": "Создание файла binary_io.py с примерами работы с двоичными данными", "_requires_user_approval": true}